import React from "react";
import { useNavigate, Link } from "react-router-dom";
import "./index.css"; // Import the CSS file for styling
import { auth, googleAuthProvider } from "./firebase"; // Import Firebase configuration

export default function Loggin() {
  const navigate = useNavigate();

  const handleLogin = async (event) => {
    event.preventDefault();
    // Handle regular login here (if needed)
    navigate("/dashboard");
  };

  const handleGoogleLogin = async () => {
    try {
      const result = await auth.signInWithPopup(googleAuthProvider);
      // Handle user information or redirect after successful login
      navigate("/dashboard");
    } catch (error) {
      console.error("Error during Google login:", error.message);
    }
  };

  return (
    <div id="logginContainer">
      <div id="logoTitle">Welcome to Stock Predictor</div>
      <form onSubmit={handleLogin} className="logginComponentContainer">
        <div className="usernameClass">
          <label htmlFor="username">Username: </label>
          <input type="text" id="username" name="username" />
          <br />
          <br />
          <label htmlFor="password">Password: </label>
          <input type="password" id="password" name="password" />
          <br />
          <br />
          <input type="submit" value="Submit" />
          <div className="loginWithFirebase" onClick={handleGoogleLogin}>
            Login with Google
          </div>
        </div>
      </form>
      <div className="newUserText">
        For new users, create an account!
        <Link to="/signup"> Sign Up</Link>
      </div>
    </div>
  );
}
