import React from "react";
import { useNavigate, Link } from "react-router-dom";
import { useAuth } from "../../Autcontent/index"; // Use the useAuth hook to access AuthContext
import "./index.css"; // Import the CSS file for styling

export default function Loggin() {
  const navigate = useNavigate();
  const { login, signupWithGoogle } = useAuth(); // Destructure methods from useAuth

  const handleLogin = async (event) => {
    event.preventDefault();
    const email = event.target.username.value;
    const password = event.target.password.value;

    try {
      await login(email, password); // Use the login function from AuthProvider
      navigate("/dashboard");
    } catch (error) {
      console.error("Error logging in:", error);
      // Handle error (e.g., show an error message)
    }
  };

  // Google login function
  const handleGoogleLogin = async () => {
    try {
      await signupWithGoogle(); // Use signupWithGoogle from AuthProvider
      navigate("/dashboard"); // Redirect after successful login
    } catch (error) {
      console.error("Error during Google login:", error);
      // Handle error (e.g., show an error message)
    }
  };

  return (
    <div id="logginContainer">
      <div id="logoTitle">Welcome to Stock Predictor</div>
      <form onSubmit={handleLogin} className="logginComponentContainer">
        <div className="usernameClass">
          <label htmlFor="username">Username: </label>
          <input type="text" id="username" name="username" />
          <br />
          <br />
          <label htmlFor="password">Password: </label>
          <input type="password" id="password" name="password" />
          <br />
          <br />
          <input type="submit" value="Submit" />
        </div>
        <div className="loginWithFirebase">
          <button type="button" onClick={handleGoogleLogin}>
            Login with Google
          </button>
        </div>
      </form>

      <div className="newUserText">
        For new users, create an account!
        <Link to="/signup"> Sign Up</Link>
      </div>
    </div>
  );
}
