import React, { useContext, useState, useEffect } from "react";
import { auth, provider } from "../../firebase";
import { useHistory } from "react-router-dom";
// import firebase from "firebase/app";
import firebase from "firebase/compat/app";

// import { API } from "../../utils/API";

const AuthContext = React.createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const delay = 5;

  const [currentUser, setCurrentUser] = useState();
  const [loading, setLoading] = useState(true);
  const [show, setShow] = useState(false);

  useEffect(() => {
    setTimeout(() => setShow("Time has passed"), 10000);
    console.log("TIMER: " + show);
  }, [show]);

  const history = useHistory();

  async function signupWithGoogle() {
    auth
      .signInWithPopup(provider)
      .then((result) => (result.additionalUserInfo.isNewUser ? result : false))
      .then((result) => {
        if (result) {
          const newUser = {
            userName: result.user.displayName,
            profileImage: [],
            uid: result.user.uid,
            hasPassword: false,
          };

          console.log(newUser);
          API.saveUser(newUser);
        } else {
          console.log("I'm Sorry, I'm afraid I can't do that.");
        }
      });
  }

  function signup(name, email, password) {
    return auth
      .createUserWithEmailAndPassword(email, password)
      .then((result) => (result.additionalUserInfo.isNewUser ? result : false))
      .then((result) => {
        if (result) {
          result.user.updateProfile({ displayName: name });
          result.user.sendEmailVerification();

          const newUser = {
            userName: name,
            profileImage: [],
            uid: result.user.uid,
            hasPassword: true,
          };

          console.log(newUser);
          API.saveUser(newUser);
        } else {
          console.log("I'm Sorry, I'm afraid I can't do that.");
        }
      });
  }

  async function login(email, password) {
    return auth.signInWithEmailAndPassword(email, password);
  }

  function logout() {
    return auth.signOut();
  }

  function resetPassword(email) {
    return auth.sendPasswordResetEmail(email);
  }

  function updateEmail(email) {
    return currentUser.updateEmail(email);
  }

  function updatePassword(password) {
    console.log("update password function");
    return currentUser.updatePassword(password);
  }

  function updatePhotoURL(image) {
    return currentUser.updateProfile({ photoURL: image });
  }

  function updateDisplayName(name) {
    return currentUser.updateProfile({ displayName: name });
  }

  const reauthenticate = (currentPassword) => {
    const user = firebase.auth().currentUser;
    const cred = firebase.auth.EmailAuthProvider.credential(
      user.email,
      currentPassword
    );
    return user.reauthenticateWithCredential(cred);
  };

  const changePassword = async (oldPassword, newPassword) => {
    const user = firebase.auth().currentUser;
    try {
      // reauthenticating
      await reauthenticate(oldPassword);
      // updating password
      await user.updatePassword(newPassword);
    } catch (err) {
      console.log(err);
      // logout()
    }
  };

  function deleteAccount() {
    console.log("delete function");
    // const auth = getAuth();
    // const user = auth.currentUser;

    // deleteUser(user).then(() => {
    //   // User deleted.
    // }).catch((error) => {
    //   // An error ocurred
    //   // ...
    // });

    const user = firebase.auth().currentUser;

    user
      .delete()
      .then(() => {
        // User deleted.
      })
      .catch((error) => {
        // alert(error)
        // An error ocurred
        // ...
      });
  }

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setCurrentUser(user);
      setLoading(false);
    });

    return unsubscribe;
  }, []);

  const value = {
    currentUser,
    login,
    signup,
    logout,
    resetPassword,
    updateEmail,
    updatePassword,
    updatePhotoURL,
    signupWithGoogle,
    changePassword,
    reauthenticate,
    updateDisplayName,
    deleteAccount,
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
}
